/* Load this script using conditional IE comments if you need to support IE 7 and IE 6. */

window.onload = function() {
	function addIcon(el, entity) {
		var html = el.innerHTML;
		el.innerHTML = '<span style="font-family: \'canary\'">' + entity + '</span>' + html;
	}
	var icons = {
			'icon-previous' : '&#x70;&#x72;&#x65;&#x76;&#x69;&#x6f;&#x75;&#x73;',
			'icon-next' : '&#x6e;&#x65;&#x78;&#x74;',
			'icon-menu' : '&#x6d;&#x65;&#x6e;&#x75;',
			'icon-bubble' : '&#x62;&#x75;&#x62;&#x62;&#x6c;&#x65;',
			'icon-canary-bird' : '&#x63;&#x61;&#x6e;&#x61;&#x72;&#x79;',
			'icon-search' : '&#x73;&#x65;&#x61;&#x72;&#x63;&#x68;'
		},
		els = document.getElementsByTagName('*'),
		i, attr, html, c, el;
	for (i = 0; ; i += 1) {
		el = els[i];
		if(!el) {
			break;
		}
		attr = el.getAttribute('data-icon');
		if (attr) {
			addIcon(el, attr);
		}
		c = el.className;
		c = c.match(/icon-[^\s'"]+/);
		if (c && icons[c[0]]) {
			addIcon(el, icons[c[0]]);
		}
	}
};